import{_ as r,c as n,o as i,af as s}from"./chunks/framework.Byp1uneH.js";const c=JSON.parse('{"title":"Im Gold des Käfigs","description":"","frontmatter":{"title":"Im Gold des Käfigs","published_at":"2025-07-20T11:55Z"},"headers":[],"relativePath":"essay/im-gold-des-kaefigs.md","filePath":"essay/im-gold-des-kaefigs.md"}'),t={name:"essay/im-gold-des-kaefigs.md"};function a(d,e,b,h,l,u){return i(),n("div",null,e[0]||(e[0]=[s('<h1 id="im-gold-des-kafigs" tabindex="-1">Im Gold des Käfigs <a class="header-anchor" href="#im-gold-des-kafigs" aria-label="Permalink to &quot;Im Gold des Käfigs&quot;">​</a></h1><h2 id="der-reflex-des-zeigens" tabindex="-1">Der Reflex des Zeigens <a class="header-anchor" href="#der-reflex-des-zeigens" aria-label="Permalink to &quot;Der Reflex des Zeigens&quot;">​</a></h2><p>Einige wenige genügen,<br> um das Gleichgewicht zu stören.<br> Ein paar Jugendliche schlagen einen Obdachlosen bewusstlos.<br> Irgendwo.<br> Gefilmt mit einem Handy.<br> Ein Video,<br> das sich rasend verbreitet –<br> weil es so verstörend ist.<br> Oder so erwartbar.</p><p>Ein Mann rast mit seinem SUV in eine Menschenmenge.<br> Ein Schüler bringt eine Waffe mit zur Schule.</p><p>Wir zeigen auf sie.<br> Auf die Täter.<br> Auf die Ausreißer.<br> Auf die,<br> die „anders“ sind.<br> „Einige wenige gefährden das Ganze“,<br> sagen wir.</p><p>Aber:<br> Auf wen zeigen wir da eigentlich?<br> Und wer hat entschieden,<br> dass genau dort unser Blick hingehört?</p><p>Vielleicht war es ein Reel,<br> ein Short,<br> ein Snap,<br> ein Kommentar.<br> Ein Algorithmus,<br> der wusste,<br> was uns empört.<br> Vielleicht auch nur ein Gefühl –<br> ausgelöst von Bildern,<br> die uns längst jemand vorsortiert hat.</p><h2 id="erschopfende-emporung" tabindex="-1">Erschöpfende Empörung <a class="header-anchor" href="#erschopfende-emporung" aria-label="Permalink to &quot;Erschöpfende Empörung&quot;">​</a></h2><p>Wir zeigen.<br> Nicht, weil wir sicher sind.<br> Sondern weil wir müde sind.<br> Weil Nachdenken Kraft kostet.<br> Weil Zwischentöne Zeit brauchen –<br> und Zeit ist heute eine Zumutung.</p><p>Der Alltag hält uns in Bewegung.<br> Schneller,<br> lauter,<br> mehr.<br> Und während wir funktionieren,<br> wird sie uns abtrainiert,<br> unsere Fähigkeit,<br> zwischen den Zeilen zu lesen.<br> Nicht weil wir dumm sind –<br> sondern weil wir abgelenkt werden.</p><p>Vielleicht ist genau das die Absicht.<br> Dass wir müde bleiben.<br> Gehetzt.<br> Oberflächlich informiert.<br> Zugänglich für einfache Wahrheiten.<br> Nicht, weil sie wahr sind –<br> sondern weil sie schnell gehen.</p><h2 id="die-einrichtung-der-ohnmacht" tabindex="-1">Die Einrichtung der Ohnmacht <a class="header-anchor" href="#die-einrichtung-der-ohnmacht" aria-label="Permalink to &quot;Die Einrichtung der Ohnmacht&quot;">​</a></h2><p>Und vielleicht –<br> ganz vielleicht –<br> sind es wieder einige wenige,<br> die davon profitieren.</p><p>Die es einrichten.<br> Die uns beschäftigt halten mit den Falschen,<br> damit wir die Richtigen übersehen.</p><p>Wir alle tragen Muster in uns,<br> die wir nicht selbst gewählt haben.<br> Manche nennen es Erziehung,<br> andere Herkunft.<br> Vielleicht begegnen wir der Welt auf eine Weise,<br> die wir nie hinterfragt,<br> sondern nur übernommen haben.<br> Und wenn wir –<br> unter dem Lärm des Alltags –<br> nie innehalten,<br> innehalten -<br> sie zu entschlüsseln,<br> setzen wir fort,<br> was uns selbst einmal gefehlt hat.</p><p>Denn der Versuch vieler,<br> dem zu entkommen,<br> was man Herkunft nennt,<br> endet oft schon am Anfang.<br> In einer Welt,<br> in der Bildung als Gut gehandelt wird -<br> Perspektiven an Eigentum gekoppelt werden,<br> verkommen Moral und Würde zu Luxusgütern.</p><p>Wer nichts hat,<br> soll wenigstens funktionieren.<br> Wer ausbricht,<br> gilt als defekt.</p><p>Und während wir weiterhin auf jene deuten,<br> die aus der Reihe tanzen,<br> bleiben die wahren „einigen wenigen“ im Schatten.<br> Nicht jene,<br> die sich am System vergreifen –<br> sondern jene,<br> die es eingerichtet haben.</p><h2 id="der-luxus-der-entfremdung" tabindex="-1">Der Luxus der Entfremdung <a class="header-anchor" href="#der-luxus-der-entfremdung" aria-label="Permalink to &quot;Der Luxus der Entfremdung&quot;">​</a></h2><p>Vielleicht sitzen sie nicht auf Anklagebänken,<br> sondern in Vorstandsetagen.<br> Vielleicht brauchen sie keine Gewalt –<br> weil ihnen Strukturen,<br> Werkzeuge,<br> Erbschaften zur Verfügung stehen.</p><p>Vielleicht handeln sie nicht aus Not,<br> sondern aus Gewohnheit.<br> Hineingeboren in Einfluss,<br> in ein Netz aus Erwartungen und Möglichkeiten,<br> so dicht geknüpft,<br> dass es kaum noch Raum lässt -<br> für Bewusstsein.</p><p>Vielleicht könnten sie teilen.<br> Aber was,<br> wenn ihr Verhalten sie nicht lässt?</p><p>Was,<br> wenn ihr Reichtum nicht Ausdruck von Freiheit ist,<br> sondern von Abhängigkeit?</p><p>Was,<br> wenn sie mehr brauchen,<br> weil sie weniger fühlen?<br> Wenn Konsum zur Betäubung wird,<br> Macht zur Ersatzreligion?</p><p>Könnte ihr Verhalten ein schleichender Selbstmord sein?<br> Ein sägender Zweifel an der Grundlage,<br> auf der alles ruht –<br> Vertrauen,<br> Gemeinschaft,<br> Erde?</p><p>Vielleicht gäbe es ohne die Vielen keine Wenigen.<br> Und doch wird das verdrängt,<br> ignoriert,<br> rationalisiert –<br> bis womöglich nichts mehr bleibt,<br> das sich noch rechtfertigen ließe.</p><p>Und genau das imitieren –<br> mit Ausnahme einiger weniger –<br> die Vielen.<br> In ihrer Bewusstlosigkeit konsumieren sie,<br> was ihnen angeboten wird.<br> Leben,<br> was ihnen vorgelebt wird.<br> Vergessen,<br> was sie verlieren.<br> Und berauben sich dabei selbst –<br> der Lebensgrundlage,<br> der Zukunft,<br> der Welt.</p><p>Einige wenige.<br> Nicht immer die,<br> auf die wir zuerst zeigen.<br> Aber vielleicht genau die,<br> die wollen,<br> dass wir es tun.</p><h2 id="das-leise-andere" tabindex="-1">Das leise Andere <a class="header-anchor" href="#das-leise-andere" aria-label="Permalink to &quot;Das leise Andere&quot;">​</a></h2><p>Und doch –<br> gibt es auch andere.</p><p>Einige wenige,<br> die nicht lauter werden,<br> sondern klarer.<br> Die nicht zeigen,<br> sondern zuhören.<br> Die nicht folgen,<br> sondern fragen.</p><p>Nicht aus Überlegenheit.<br> Sondern aus Aushalten.</p><p>Vielleicht erinnern sie daran,<br> dass wir anders könnten.</p><p>Nicht aus Hoffnung.<br> Aber aus Ahnung.</p><p>Vielleicht genügt das -<br> für den Moment.</p>',35)]))}const o=r(t,[["render",a]]);export{c as __pageData,o as default};
