import{_ as r,c as t,o as i,ae as a}from"./chunks/framework.Dgg8-8ov.js";const u=JSON.parse('{"title":"Im Käfig der Wahrnehmung","description":"","frontmatter":{"title":"Im Käfig der Wahrnehmung"},"headers":[],"relativePath":"essay/im-kaefig-der-wahrnehmung.md","filePath":"essay/im-kaefig-der-wahrnehmung.md"}'),n={name:"essay/im-kaefig-der-wahrnehmung.md"};function s(o,e,h,l,c,d){return i(),t("div",null,e[0]||(e[0]=[a('<h1 id="vorwort" tabindex="-1">Vorwort <a class="header-anchor" href="#vorwort" aria-label="Permalink to &quot;Vorwort&quot;">​</a></h1><blockquote><p><em>Soulilog ist kein Ort für schnelle Antworten.</em><br><em>Es ist ein Raum für das, was zwischen den Zeilen lebt –<br> für Gedanken, die sich weigern, vereinfacht zu werden.</em></p></blockquote><p>Ich schreibe, weil das Schweigen zu laut geworden ist.<br> Weil das Urteil oft schneller kommt als das Verstehen.<br> Weil die Wahrheit manchmal leiser spricht als der Vorwurf.</p><p>Hier findest du keine Parolen. Keine Schlagworte.<br> Nur Worte, die versuchen, ehrlich zu sein.</p><h2 id="schlusswort" tabindex="-1">Schlusswort <a class="header-anchor" href="#schlusswort" aria-label="Permalink to &quot;Schlusswort&quot;">​</a></h2><p>Vielleicht wirst du dich in manchen Zeilen wiederfinden.<br> Vielleicht auch nicht.<br><strong>Beides ist erlaubt.</strong></p><hr><p><em>Ein Flüstern im Sturm der Meinungen.</em></p>',8)]))}const f=r(n,[["render",s]]);export{u as __pageData,f as default};
