import{_ as n,c as i,o as r,ae as t}from"./chunks/framework.Dgg8-8ov.js";const d=JSON.parse('{"title":"Megalopolitanischer Soulilog","description":"","frontmatter":{"title":"Megalopolitanischer Soulilog","published_at":"2025-07-10T20:00Z"},"headers":[],"relativePath":"essay/megalopolitanischer-soulilog.md","filePath":"essay/megalopolitanischer-soulilog.md"}'),a={name:"essay/megalopolitanischer-soulilog.md"};function s(l,e,h,o,c,p){return r(),i("div",null,e[0]||(e[0]=[t('<h1 id="megalopolitanischer-soulilog" tabindex="-1">Megalopolitanischer Soulilog <a class="header-anchor" href="#megalopolitanischer-soulilog" aria-label="Permalink to &quot;Megalopolitanischer Soulilog&quot;">​</a></h1><p><em>Arno:</em><br> Ich glaube an die Wunde, die sich nicht versteckt.<br> Weil sie wahr ist.<br> Und weil sie spricht.</p><p><em>Rand:</em><br> Wahrheit ist irrelevant, wenn sie nicht funktioniert.<br> Dein Glaube ist schön – aber nutzlos.</p><p><em>Arno:</em><br> Du nennst es nutzlos, weil es nicht messbar ist.<br> Aber was nicht messbar ist, kann tragen.<br> Es trägt mich. Und viele andere.</p><p><em>Rand:</em><br> Es trägt dich durch das Gefühl.<br> Nicht durch die Welt.</p><p><em>Arno:</em><br> Vielleicht ist Gefühl genau das,<br> was in deiner Welt fehlt.</p><p><em>Rand:</em><br> Gefühl macht schwach.<br> Ordnung macht stabil.</p><p><em>Arno:</em><br> Ordnung ohne Mitgefühl ist nur Stillstand mit Struktur.<br> Ein perfekter Käfig.</p><p><em>Rand:</em><br> Manche nennen es Heimat.</p><p><em>Arno:</em><br> Nicht, wenn man darin verhungert.</p><p><em>Rand:</em><br> Du idealisierst das Leiden.<br> Aber Leiden bringt keine Systeme hervor.<br> Nur Träume.</p><p><em>Arno:</em><br> Und Träume bringen Menschen hervor.<br> Oder retten, was von ihnen übrig bleibt.</p><p><em>Rand:</em><br> Menschen brauchen Halt. Nicht Trost.</p><p><em>Arno:</em><br> Vielleicht ist Trost der einzige Halt, den man geben kann,<br> wenn alles andere fällt.</p><p><em>Rand:</em><br> Du willst berühren. Ich will bewegen.</p><p><em>Arno:</em><br> Bewegung ohne Richtung ist Flucht.<br> Ich bleibe. Und höre zu.</p><p><em>Rand:</em><br> Dann wirst du untergehen.</p><p><em>Arno:</em><br> Vielleicht.<br> Aber vielleicht wächst aus dem, was untergeht, etwas,<br> das du nie vorhersehen kannst.</p><p><em>Rand:</em><br> Ich verlasse mich nicht auf Vielleicht.</p><p><em>Arno:</em><br> Ich schon.<br> Denn im Vielleicht wohnt der Mensch.</p><p>- Stille -</p><p>Nicht aus Einigkeit.<br> Sondern weil beide wissen: Worte reichen nicht.</p><hr><blockquote><p>Vielleicht ist es nicht die Aufgabe, zu wissen.<br> Sondern auszuhalten.</p><p>Nicht zu wählen zwischen Unsichtbarkeit und Sichtbarkeit –<br> sondern zu leben am Rand beider Zustände.</p><p>Der Käfig ist echt.<br> Die Idee seiner Öffnung auch.</p><p>Und wer beides sieht,<br> ist weder schwach noch erleuchtet –</p><p>sondern verantwortlich.</p></blockquote><hr><p>Ein Satz fällt.<br> Ein Stein in ein noch unfertiges Fundament.</p><p>Er blickt auf die Trümmer.<br> Sie auf das Fundament.<br> Zwischen beiden: ein Raum.<br> Vielleicht Stadt. Vielleicht Riss.</p><p>- <em>Vielleicht war der Käfig nie wirklich verschlossen. Vielleicht hat nur niemand je nach dem Schlüssel gesucht.</em></p>',28)]))}const m=n(a,[["render",s]]);export{d as __pageData,m as default};
