import{_ as r,c as i,o as n,ae as s}from"./chunks/framework.Dgg8-8ov.js";const b=JSON.parse('{"title":"Ein Flüstern im Sturm der Meinungen","description":"","frontmatter":{"title":"Ein Flüstern im Sturm der Meinungen","text":"Bevor der Zeigefinger spricht."},"headers":[],"relativePath":"index.md","filePath":"index.md"}'),t={name:"index.md"};function a(l,e,o,d,h,c){return n(),i("div",null,e[0]||(e[0]=[s('<h1 id="prolog" tabindex="-1">Prolog <a class="header-anchor" href="#prolog" aria-label="Permalink to &quot;Prolog&quot;">​</a></h1><blockquote><p><em>Soulilog ist kein Ort für schnelle Antworten.</em><br><em>Es ist ein Raum für das, was zwischen den Zeilen lebt –<br> für Gedanken, die sich weigern, vereinfacht zu werden.</em></p></blockquote><p>Ich schreibe, weil das Schweigen zu laut geworden ist.<br> Weil das Urteil oft schneller kommt als das Verstehen.<br> Weil die Wahrheit manchmal leiser spricht als der Vorwurf.</p><p>Ich weiß nicht, ob das hier gelesen wird –<br> und vielleicht ist das auch egal.<br> Aber irgendwo muss es raus.</p><p>Früher war mein Antrieb der Schmerz.<br> Heute ist es die Leere.<br> Ich schreibe, weil ich nicht verstummen will.</p><p>Vielleicht schreibe ich das hier nicht, um etwas zu verändern.<br> Vielleicht schreibe ich es, um nicht selbst zu verrohen.<br> Um dem etwas entgegenzusetzen, was mich sonst innerlich zerfrisst:<br> diese laute, aggressive Welt,<br> die Schwäche nur duldet, wenn sie vermarktet werden kann.</p><h2 id="warum-soulilog" tabindex="-1">Warum Soulilog? <a class="header-anchor" href="#warum-soulilog" aria-label="Permalink to &quot;Warum Soulilog?&quot;">​</a></h2><p><strong>„Im Käfig der Wahrnehmung“</strong> war der Anfang –<br> ein Essay über vorschnelle Urteile,<br> über Kunst, die angreift, weil sie verletzlich ist.<br> Über die Schwierigkeit, sich Gehör zu verschaffen,<br> wenn alle schon entschieden haben, was sie hören wollen.</p><p>Aus diesem Text wurde mehr.<br> Ein Bedürfnis. Ein Raum.<br> Ein Ort, an dem Gedanken atmen dürfen.</p><p><strong>Soulilog</strong> ist ein Kunstwort, geboren aus Gefühl und Reflexion.<br> Es trägt die Seele im Klang – <em>soul</em> –,<br> und zugleich den Ursprung des Selbstgesprächs:<br><strong>„soliloque“</strong>, das französische Wort für ein inneres Zwiegespräch,<br> das nicht für andere gedacht ist,<br> und gerade deshalb etwas Wahres zeigt.</p><p>Ein Logbuch der leisen Töne.<br> Ein Protokoll der Gedanken,<br> bevor sie von der Welt verbogen werden.</p><p>Hier findest du keine Parolen. Keine Schlagworte.<br> Nur Worte, die versuchen, ehrlich zu sein.</p><p>Vielleicht wirst du dich in manchen Zeilen wiederfinden.<br> Vielleicht auch nicht.</p><p><strong>Beides ist erlaubt.</strong></p>',14)]))}const g=r(t,[["render",a]]);export{b as __pageData,g as default};
